    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if(cboCriteria.getSelectedItem().equals("Crime Increase/Decrease"))
        {
            
            dispose();
            new frmDecisionCrimes().setVisible(true);
        }
        
        else if(cboCriteria.getSelectedItem().equals("Investigations completed Decrease/Increase"))
        {
            
            dispose();
            new frmDecisionInvestigationCom_Dec().setVisible(true);
        }
        
        else if(cboCriteria.getSelectedItem().equals("Missing Persons Increase/Decrease"))
        {
            dispose();
            new frmDecisionCrimes().setVisible(true);
        }
        else  if(cboCriteria.getSelectedItem().equals("Dispatch Increase/Decrease"))
        {
            
            dispose();
            new frmDecisionCrimes().setVisible(true);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

